import React, { useEffect, useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import {
  faGithub,
  faInstagram,
  faLinkedin,
  faMedium,
  faStackOverflow,
  faWhatsapp,
} from "@fortawesome/free-brands-svg-icons";
import { Box, HStack } from "@chakra-ui/react";
import { faUser } from "@fortawesome/free-regular-svg-icons";

const socials = [
  {
    icon: faUser,
    url: "https://monishwar.com/",
  },
  {
    icon: faGithub,
    url: "https://github.com/monishwarmc",
  },
  {
    icon: faLinkedin,
    url: "https://www.linkedin.com/in/monishwar/",
  },
  {
    icon: faMedium,
    url: "https://medium.com",
  },
  {
    icon: faStackOverflow,
    url: "https://stackoverflow.com/users/21341696/monishwar-m-c",
  },
];



const Header = () => {
  const handleClick = (anchor) => () => {
    const id = `${anchor}-section`;
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  };

  return (
    <Box
      position="fixed"
      top={0}
      left={0}
      right={0}
      translateY={0}
      transitionProperty="transform"
      transitionDuration=".3s"
      transitionTimingFunction="ease-in-out"
      backgroundColor="#18181b"
    >
      <Box color="white" maxWidth="1280px" margin="0 auto">
        <HStack
          px={16}
          py={4}
          justifyContent="space-between"
          alignItems="center"
        >
          <nav>
          <HStack spacing={8}
              >
            {socials.map((social, i) => (

              <a key={i} href={social.url}
              >
                <FontAwesomeIcon icon={social.icon} size="4x"  />
              </a>
            ))}</HStack>
          </nav>

          <nav>
            <HStack spacing={8}>
              {/* Add links to Projects and Contact me section */}
              <a href="https://wa.me/919360124835">
                <FontAwesomeIcon icon={faWhatsapp} size="4x" />
              </a>
              <a href="https://www.linkedin.com/in/monishwar/">
                <FontAwesomeIcon icon={faInstagram} size="4x" />
              </a>
              <a href="https://medium.com/@monishwarmc">
                <FontAwesomeIcon icon={faMedium} size="4x" />
              </a>
              <a href="https://stackoverflow.com/users/21341696/monishwar-m-c">
                <FontAwesomeIcon icon={faStackOverflow} size="4x" />
              </a>
            </HStack>
          </nav>
        </HStack>
      </Box>
    </Box>
  );
};
export default Header;
